function initAnimations(){anime({targets:".dot",translateY:[-20,0],opacity:1,direction:"alternate",loop:!0,duration:1e3,easing:"spring(1, 80, 10, 0)",delay:function(t,n,e){return 100*n},endDelay:function(t,n,e){return 100*(e-n)}}),anime({targets:".window",opacity:[0,1],translateX:["-50%","-50%"],translateY:["-50%","-50%"],perspective:["800px","800px"],rotateX:[150,0],rotateY:[20,0],easing:"spring(0.5, 80, 5, 0)"}),anime({targets:".link",translateY:[-20,0],opacity:1,direction:"alternate",loop:!1,duration:1e3,easing:"spring(1, 80, 10, 0)",delay:function(t,n,e){return 100+25*n},endDelay:function(t,n,e){return 25*(e-n)}})}