const subtitles=["Creating immersive multiplayer experiences and scalable software solutions","Game Developer & Software Engineer - Crafting digital worlds since age 13","Building the future of interactive entertainment and enterprise applications","Unity expert, full-stack developer, and creative problem solver","From game jams to enterprise systems - passionate about great code"];function initializeLandingPage(){setTimeout(()=>{setRandomSubtitle()},800);document.querySelectorAll(".persona-card").forEach(e=>{e.addEventListener("mouseenter",()=>{e.style.boxShadow="0 10px 30px rgba(107, 114, 128, 0.3)"}),e.addEventListener("mouseleave",()=>{e.style.boxShadow=""})})}function setRandomSubtitle(){const e=document.querySelector(".landing-subtitle");if(!e)return;const t=Math.floor(Math.random()*subtitles.length);e.textContent=subtitles[t]}window.LandingPage={initialize:initializeLandingPage};